
public class ConditionalApp5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 1;
		
		while ( n <= 10 ){
			System.out.print ( n );
			
			if ( n >= 3 && n < 10 ) {
				System.out.print(" - OK");
			}
			n ++;
			
			System.out.println();
			
			
		}
		
	}

}
